using System;
using System.Runtime.Intrinsics.X86;
using Microsoft.VisualStudio.TestTools.UnitTesting;

using Canti.Data;
using Canti.Blockchain.Crypto.AES;

namespace Tests
{
    [TestClass]
    public class AESTests
    {
        [TestMethod]
        public void TestAESBSingleRound()
        {
            byte[] expandedKeys1 = Encoding.HexStringToByteArray(
                ""
            );

            byte[] input1 = Encoding.HexStringToByteArray(
                "f77f10a5fb1a52a2b466751c3a858dc3"
            );

            if (Aes.IsSupported)
            {
                AES.AESBSingleRound(expandedKeys1, input1);

                Assert.AreEqual<string>("d3f15e41a16af6931839a8e818cb9abd", Encoding.ByteArrayToHexString(input1));
            }

            input1 = Encoding.HexStringToByteArray(
                "f77f10a5fb1a52a2b466751c3a858dc3"
            );

            /* Test the non intrinsics version */
            AES.AESBSingleRound(expandedKeys1, input1, false);

            Assert.AreEqual<string>("d3f15e41a16af6931839a8e818cb9abd", Encoding.ByteArrayToHexString(input1));


            byte[] expandedKeys2 = Encoding.HexStringToByteArray(
                ""
            );

            byte[] input2 = Encoding.HexStringToByteArray(
                "f6ab72cec832b4070711b7e3b8700d6a"
            );

            if (Aes.IsSupported)
            {
                AES.AESBSingleRound(expandedKeys2, input2);

                Assert.AreEqual<string>("e59c503c34b319b45b66a344316cd109", Encoding.ByteArrayToHexString(input2));
            }

            input2 = Encoding.HexStringToByteArray(
                "f6ab72cec832b4070711b7e3b8700d6a"
            );

            AES.AESBSingleRound(expandedKeys2, input2, false);

            Assert.AreEqual<string>("e59c503c34b319b45b66a344316cd109", Encoding.ByteArrayToHexString(input2));


            byte[] expandedKeys3 = Encoding.HexStringToByteArray(
                ""
            );

            byte[] input3 = Encoding.HexStringToByteArray(
                "ac2432e2f84f5f244ef7e5d977c9f19e"
            );

            if (Aes.IsSupported)
            {
                AES.AESBSingleRound(expandedKeys3, input3);

                Assert.AreEqual<string>("16767345c5adc04004b973481f5682c3", Encoding.ByteArrayToHexString(input3));
            }

            input3 = Encoding.HexStringToByteArray(
                "ac2432e2f84f5f244ef7e5d977c9f19e"
            );

            AES.AESBSingleRound(expandedKeys3, input3, false);

            Assert.AreEqual<string>("16767345c5adc04004b973481f5682c3", Encoding.ByteArrayToHexString(input3));
        }

        [TestMethod]
        public void TestAESBPseudoRound()
        {
            byte[] expandedKeys1 = Encoding.HexStringToByteArray(
                ""
            );

            byte[] input1 = Encoding.HexStringToByteArray(
                "f77f10a5fb1a52a2b466751c3a858dc3"
            );

            AES.AESBPseudoRound(expandedKeys1, input1, 0);

            Assert.AreEqual<string>("27bfdeec6a2f52b0e815e04a31b40b7f", Encoding.ByteArrayToHexString(input1));


            byte[] expandedKeys2 = Encoding.HexStringToByteArray(
                ""
            );

            byte[] input2 = Encoding.HexStringToByteArray(
                "f6ab72cec832b4070711b7e3b8700d6a"
            );

            AES.AESBPseudoRound(expandedKeys2, input2, 0);

            Assert.AreEqual<string>("ad2bd997286ac6a1cc6260a4037448ab", Encoding.ByteArrayToHexString(input2));


            byte[] expandedKeys3 = Encoding.HexStringToByteArray(
                ""
            );

            byte[] input3 = Encoding.HexStringToByteArray(
                "ac2432e2f84f5f244ef7e5d977c9f19e"
            );

            AES.AESBPseudoRound(expandedKeys3, input3, 0);

            Assert.AreEqual<string>("463367a3ac698a8504d5d3267e7a309d", Encoding.ByteArrayToHexString(input3));
        }

        [TestMethod]
        public void TestAESPseudoRound()
        {
            byte[] expandedKeys1 = Encoding.HexStringToByteArray(
                ""
            );

            byte[] input1 = Encoding.HexStringToByteArray(
                ""
            );

            if (Sse2.IsSupported && Aes.IsSupported)
            {
                AES.AESPseudoRound(expandedKeys1, input1);

                Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(input1));
            }

            input1 = Encoding.HexStringToByteArray(
                ""
            );

            /* Now test without using intrinsics */
            AES.AESPseudoRound(expandedKeys1, input1, false);

            Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(input1));


            byte[] expandedKeys2 = Encoding.HexStringToByteArray(
                ""
            );

            byte[] input2 = Encoding.HexStringToByteArray(
                ""
            );

            if (Sse2.IsSupported && Aes.IsSupported)
            {
                AES.AESPseudoRound(expandedKeys2, input2);

                Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(input2));
            }

            input2 = Encoding.HexStringToByteArray(
                ""
            );

            AES.AESPseudoRound(expandedKeys2, input2, false);

            Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(input2));


            byte[] expandedKeys3 = Encoding.HexStringToByteArray(
                ""
            );

            byte[] input3 = Encoding.HexStringToByteArray(
                ""
            );

            if (Sse2.IsSupported && Aes.IsSupported)
            {
                AES.AESPseudoRound(expandedKeys3, input3);

                Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(input3));
            }

            input3 = Encoding.HexStringToByteArray(
                ""
            );

            AES.AESPseudoRound(expandedKeys3, input3, false);

            Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(input3));
        }

        [TestMethod]
        public void TestExpandKey()
        {
            byte[] key1 = Encoding.HexStringToByteArray(
                "f42b7bbf670bbdc8872b2a74a81de5bbaa754e5dd69ae6176263598220bd7a02"
            );

            byte[] expandedKeys1;

            /* The intrinsics implementation only fills out the first 176 bytes of the result. */
            if (Sse2.IsSupported && Aes.IsSupported)
            {
                expandedKeys1 = AES.ExpandKey(key1);

                Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(expandedKeys1));
            }

            /* This time, with intrinsics disabled */
            expandedKeys1 = AES.ExpandKey(key1, false);

            Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(expandedKeys1));

            byte[] key2 = Encoding.HexStringToByteArray(
                "8220b9a023265e82716dc1a0bd55efb4ca450d6a1a874d2f175bb79bb18b280a"
            );

            byte[] expandedKeys2;

            if (Sse2.IsSupported && Aes.IsSupported)
            {
                expandedKeys2 = AES.ExpandKey(key2);

                Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(expandedKeys2));
            }

            expandedKeys2 = AES.ExpandKey(key2, false);

            Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(expandedKeys2));

            byte[] key3 = Encoding.HexStringToByteArray(
                "70dad95358fe1be0f79491573c4acc5f84bd6b19a5c5981e14541e9a5cfb780c"
            );

            byte[] expandedKeys3;

            if (Sse2.IsSupported && Aes.IsSupported)
            {
                expandedKeys3 = AES.ExpandKey(key3);

                Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(expandedKeys3));
            }

            expandedKeys3 = AES.ExpandKey(key3, false);

            Assert.AreEqual<string>("", Encoding.ByteArrayToHexString(expandedKeys3));
        }
    }
}
